mapper.update:
update board_limit set board_limit_count = board_limit_count+1
	where festival_name = #{festival_name}
	and member_id = #{member_id}

mapper.select:
select * from board_limit 
where festival_name = #{festival_name}
	and member_id = #{member_id}

select board_limit_count from board_limit
where festival_name = #{festival_name}
	and member_id = #{member_id}
